mapper.select:
select d.*, ca.name AS categoryName from dish d left outer join category ca on d.category_id = ca.id
and d.name like concat('%',#{name},'%')
and d.category_id = #{categoryId}
and d.status = #{status}
order by d.create_time desc

select * from dish
WHERE id IN

    #{id}

mapper.delete:
DELETE from dish
WHERE id IN

    #{id}
